	getAppTrayIcon : function(hasMessage) {
		var appIcon = WebAppProperties.getAppRoot();
		appIcon.append("icons");
		appIcon.append("default");
		
		if (hasMessage)
			appIcon.append(WebAppProperties.icon + ".ico");        
		else
			appIcon.append("gmail-off.png");
		
		var ioService = Cc["@mozilla.org/network/io-service;1"].getService(Ci.nsIIOService);
		var iconUri = ioService.newFileURI(appIcon);
		
		return iconUri.spec;
	},
	
	receiveMessage : function() {
		//Platform.sound().beep();
		Platform.sound().playSound("http://jasonmillerdesign.com/apps/gmail_prism/r/newmessage.wav");
		Platform.getAttention();
		n("Gmail", GoogleMail._newMailCount + " unread message"+(GoogleMail._newMailCount==1?"":"s"));
	},
	
	statusMessage : function() {
		var c = GoogleMail._newMailCount + 0;
		
		Platform.icon().title = "Gmail ("+c+")";
		Platform.icon().badgeText = c>0 ? (c+'') : null;
		
		//Platform.icon().imageSpec = GoogleMail.getAppTrayIcon(GoogleMail._newMailCount > 0);
	},
	
	_init : function() {
		GoogleMail._cnt = 0;
		GoogleMail._newMailCount = 0;
		GoogleMail._window = window;
		
		Platform.icon().imageSpec = GoogleMail.getAppTrayIcon(false);
		
		// main:
		Platform.icon().menu.addMenuItem('inbox', 'Inbox', function(){ GoogleMail._window.top.location.href='#inbox'; });
		Platform.icon().menu.addMenuItem('contacts', 'Contacts', function(){ GoogleMail._window.top.location.href='#contacts'; });
		
		// folders:
		var placesmenu = Platform.icon().menu.addSubmenu("places","Places");
		placesmenu.addMenuItem('starred', 'Starred', function(){ GoogleMail._window.top.location.href='#starred'; });
		placesmenu.addMenuItem('drafts', 'Drafts', function(){ GoogleMail._window.top.location.href='#drafts'; });
		placesmenu.addMenuItem('allmail', 'All Mail', function(){ GoogleMail._window.top.location.href='#all'; });
		placesmenu.addMenuItem('trash', 'Trash', function(){ GoogleMail._window.top.location.href='#trash'; });
		placesmenu.addMenuItem('sentmail', 'Sent Mail', function(){ GoogleMail._window.top.location.href='#sent'; });
		placesmenu.addMenuItem('spam', 'Spam', function(){ GoogleMail._window.top.location.href='#spam'; });
		
		// labels:
		var labelsmenu = Platform.icon().menu.addSubmenu("labels","Labels");
		var labels = GoogleMail._window.top.VIEW_DATA[1][1];
		for(var x=0; x<labels.length; x++) {
			if(labels[x] && labels[x][0] && !labels[x][0].match(/^\^/)) {
				(function(i) {
					var labelurl = encodeURIComponent(i+"");
					var labeltext = i+"";
					function handler() {
						GoogleMail._window.top.location.href = "#label/"+labelurl;
					}
					labelsmenu.addMenuItem(labelurl, labeltext, handler);
				})( (labels[x][0]+'').replace(/\\u0026/m,'&') );
			}
		}
		
		// check mail:
		Platform.icon().menu.addMenuItem('checkmail', 'Check Mail', function(evt){
			GoogleMail._window.top.location.href = '#gmailprism_checkingmail';
			GoogleMail._window.top.setTimeout(function(){
				if(GoogleMail._window.top.location.href.match(/\#gmailprism_checkingmail$/))
					GoogleMail._window.top.location.href = '#inbox';
			}, 1100);
		});
		Platform.icon().menu.addMenuItem('chooseservice', 'Change Service Type', function(){ GoogleMail._window.top.location.href='http://jasonmillerdesign.com/apps/gmail_prism/select.php?s=reset&reset=1'; });
	},
	
	run : function() {  
		// simple check uses the Inbox menu item to check for new mail
		
		GoogleMail._cnt++;
		
		if(GoogleMail._newMailCount == undefined)
			GoogleMail._newMailCount = 0;
		
		var title = GoogleMail._window.top.document.title;
		
		var doc = GoogleMail._window.top.document.getElementById("canvas_frame").contentWindow.document;
		var a = doc.getElementsByTagName("a");
		var matches = null;
		for(var x=0; x<a.length; x++) {
			if(a[x].innerHTML.match(/Inbox \(\d+\)/gm) && a[x].href.match(/\#inbox$/gim)) {
				matches = a[x].innerHTML.match(/Inbox \((\d+)\)/);
				break;
			}
		}
		if(!matches) {
			for(var x=0; x<a.length; x++) {
				if(a[x].innerHTML.match(/Inbox/gm) && a[x].href.match(/\#inbox$/gim)) {
					matches = ["Inbox (0)","0"];
					break;
				}
			}
		}
		
		// var matches = title.match(/Inbox \((\d+)\)/);
		if(matches) {
			if (matches[1] > GoogleMail._newMailCount) {
				GoogleMail._newMailCount = parseInt(matches[1]);
				GoogleMail.receiveMessage();
			}
			
			GoogleMail._newMailCount = parseInt(matches[1]);
		}
		else{
			var matches = title.match(/(.*) says./);
			
			if (matches) {
				n("Gmail", "You have an unread message from " + matches[1] + ".");
			}
			else{
				//GoogleMail._newMailCount = 0;
			}
		}
		
		GoogleMail.statusMessage();
		
	},
	
	startup : function() {
		GoogleMail._init();
		//GoogleMail.defaultTheme();
		// kick off a polling timer to check for new mail
		GoogleMail._timer = GoogleMail._window.setInterval(GoogleMail.run, 2000);
	},
	
	shutdown : function() {    
		if(GoogleMail._timer) {
			GoogleMail._window.clearInterval(GoogleMail._timer);
		}
	}
	
